package com.greenvest.patterns.interceptor;
public class AuthenticationInterceptor implements Interceptor {

    @Override
    public boolean preHandle(String email, String role) {
        if (email == null || email.isEmpty()) {
            System.out.println(" Access denied. User not authenticated.");
            return false;
        }
        System.out.println(" Authenticated: " + email + " | Role: " + role);
        return true;
    }
}

package com.greenvest.patterns.interceptor;

public interface Interceptor {
    boolean preHandle(String email, String role);
}

package com.greenvest.patterns.interceptor;
import java.util.ArrayList;
import java.util.List;

public class InterceptorManager {
    private List<Interceptor> list = new ArrayList<>();

    public void addInterceptor(Interceptor i) { list.add(i); }

    public boolean execute(String email, String role) {
        for (Interceptor i : list) {
            if (!i.preHandle(email, role)) return false;
        }
        return true;
    }
}

package com.greenvest.patterns.interceptor;

public class RoleInterceptor implements Interceptor {

    private String requiredRole;

    public RoleInterceptor(String requiredRole) {
        this.requiredRole = requiredRole;
    }

    @Override
    public boolean preHandle(String email, String role) {
        if (!role.equals(requiredRole)) {
            System.out.println(" Role restricted. Only " + requiredRole + " allowed.");
            return false;
        }
        return true;
    }
}

package com.greenvest.patterns.observer;


import java.util.ArrayList;
import java.util.List;

public class AlertService implements Subject {

    private List<Observer> observers = new ArrayList<>();

    @Override
    public void attach(Observer o) {
        observers.add(o);
    }

    @Override
    public void detach(Observer o) {
        observers.remove(o);
    }

    @Override
    public void notifyObservers(String msg) {
        for (Observer o : observers) o.update(msg);
    }
}

