package com.greenvest.patterns.strategy;

public interface CreditCalculationStrategy {
    int calculateCredits(double metricValue);
}

package com.greenvest.patterns.strategy;
public class RenewableEnergyStrategy implements CreditCalculationStrategy {

    @Override
    public int calculateCredits(double kwh) {

        if (kwh <= 0) return 0;

        int credits = (int) Math.ceil(kwh / 100.0);

        return Math.max(1, credits);
    }
}

package com.greenvest.patterns.strategy;

public class TreePlantingStrategy implements CreditCalculationStrategy {
    @Override
    public int calculateCredits(double metric) {

        int credits = (int) Math.ceil(metric / 10.0);

        return Math.max(1, credits);   //
    }

}

