package com.greenvest.service;

import com.greenvest.config.SystemConfig;
import com.greenvest.model.*;
import com.greenvest.repo.*;
import com.greenvest.patterns.factory.*;

import java.util.List;

public class AdminService {

    private ActionRepository actionRepo;
    private CreditRepository creditRepo;
    private ActivityService activityService = new ActivityService();

    public AdminService(ActionRepository aRepo, CreditRepository cRepo) {
        this.actionRepo = aRepo;
        this.creditRepo = cRepo;
    }

    public List<SustainabilityAction> getPendingActions() {
        return actionRepo.getPendingActions();
    }

    public void approveAction(SustainabilityAction action) {

        action.approve();
        actionRepo.update(action);

        Credit credit = CreditFactory.createCredit(
                action.getSellerEmail(),
                action.getType(),
                action.getMetricValue()
        );

        creditRepo.save(credit);

        activityService.log(
                "Approved sustainability action for seller: " + action.getSellerEmail(),
                "ADMIN"
        );
    }

    public void rejectAction(SustainabilityAction action) {

        action.reject();
        actionRepo.update(action);

        activityService.log(
                "Rejected sustainability action for seller: " + action.getSellerEmail(),
                "ADMIN"
        );
    }

    public List<SustainabilityAction> getApprovedActions() {
        return actionRepo.getApprovedActions();
    }

    public List<SustainabilityAction> getRejectedActions() {
        return actionRepo.getRejectedActions();
    }

    public void setMinimumCreditPrice(double price) {
        SystemConfig.setMinCreditPrice(price);

        activityService.log(
                "Minimum credit price updated to " + price,
                "ADMIN"
        );
    }
}

package com.greenvest.service;
import com.greenvest.model.User;
import com.greenvest.repo.UsersRepository;
import java.security.MessageDigest;
import java.util.List;

public class AuthService {

    private UsersRepository repo = UsersRepository.getInstance();

    public boolean register(String name, String email, String password, String role) {
        List<User> users = repo.loadUsers();

        for (User u : users) {
            if (u.getEmail().equalsIgnoreCase(email)) return false;
        }

        String hashed = hash(password);
        users.add(new User(name, email, role, hashed, 0.0));
        repo.saveUsers(users);
        return true;
    }

    public User login(String email, String password) {
        String hashed = hash(password);
        for (User u : repo.loadUsers()) {
            if (u.getEmail().equalsIgnoreCase(email) && u.getPasswordHash().equals(hashed)) {
                ActivityService as = new ActivityService();
                as.log("User logged in", email);
                return u;
            }

            /*if (u.getEmail().equalsIgnoreCase(email) && u.getPasswordHash().equals(hashed)) {
                return u;
                */


        }
        return null;
    }

    private String hash(String input) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            byte[] arr = md.digest(input.getBytes());
            StringBuilder sb = new StringBuilder();
            for (byte b : arr) sb.append(String.format("%02x", b));
            return sb.toString();
        } catch (Exception e) {
            return "";
        }
    }
}

package com.greenvest.service;
import com.greenvest.model.Credit;
import com.greenvest.model.Receipt;
import com.greenvest.model.User;
import com.greenvest.repo.*;
import com.greenvest.repo.ReceiptRepository;
import com.greenvest.rules.RuleEngineService;
import com.greenvest.patterns.observer.*;
import java.util.*;

import java.util.List;

public class BuyerService {

    private CreditRepository creditRepo;
    private ReceiptRepository receiptRepo;
    private PortfolioRepository portfolioRepo ;
    private AlertService alertService;
    private RuleEngineService ruleEngine;
    ActivityService activityService = new ActivityService();

    public BuyerService(CreditRepository creditRep
                        , ReceiptRepository receiptRepo
                        , PortfolioRepository portfolioRepo,
                        RuleEngineService ruleEngine) {
        this.creditRepo = creditRep;
        this.receiptRepo = receiptRepo;
        this.portfolioRepo = portfolioRepo;
        this.alertService = new AlertService();
        this.ruleEngine = ruleEngine;
    }
// ----------------MarketPlace -----------------
    public List<Credit> loadAvailableCredits() {
        return creditRepo.getAvailableCredits().stream()
                .filter(Credit::isListed)
                .filter(c -> c.getQuantity() >0)
                .toList();
    }

    public Receipt processPurchase(User buyer, Credit credit, int qty) {
        activityService.log(
                "Buyer purchased credits | Qty: " + qty + " | Credit ID: " + credit.getId(),
                buyer.getEmail()
        );

        if (!ruleEngine.validatePurchase(buyer, credit, qty)) {
            return null;
        }

        double cost = credit.getPrice() * qty;

        if (buyer.getBalance() < cost) {
            return null;
        }

        // Deduct balance
        buyer.deductBalance(cost);

        // Reduce seller credit quantity
        credit.reduceQuantity(qty);
        creditRepo.updateCredit(credit);

        // Add credit to buyer portfolio
        Credit purchased = new Credit(
                UUID.randomUUID().toString(),
                credit.getSellerEmail(),
                qty,
                credit.getPrice(),
                credit.getExpiry()
        );
        portfolioRepo.saveCreditForBuyer(buyer.getEmail(), purchased);

        //  CREATE RECEIPT BEFORE RETURN
       // double cost =credit.getPrice() *qty;
        Receipt receipt = new Receipt(
                UUID.randomUUID().toString(),
                credit.getId(),
                qty,
                cost,
                buyer.getEmail()
        );
        receiptRepo.saveReceipt(receipt);

        //  RETURN IS LAST
        return receipt;
    }

    // portfolio
    public List<Credit> getPortfolio(User buyer) {
        List<Credit> credits = portfolioRepo.getCreditsByBuyer(buyer.getEmail());

        for (Credit c : credits) {
            c.updateState();  // STATE PATTERN

            if (c.getState().equals("EXPIRED")) {
                alertService.notifyObservers("Your credit " + c.getId() + " has expired!");
            }
        }

        return credits;
    }
    // receipts
    public List<Receipt> getReceipts(User buyer) {
        return receiptRepo.getReceiptsByBuyer(buyer.getEmail());
    }

    // account summary
    public Map<String, Object> getAccountSummary(User buyer) {

        Map<String, Object> summary = new HashMap<>();

        // Buyer balance
        summary.put("balance", buyer.getBalance());

        // Load portfolio
        List<Credit> portfolio = portfolioRepo.getCreditsByBuyer(buyer.getEmail());

        int totalCredits = 0;
        int active = 0;
        int expired = 0;

        for (Credit c : portfolio) {
            c.updateState();  // Apply State Pattern

            totalCredits += c.getQuantity();

            if (c.getState().equals("ACTIVE"))
                active += c.getQuantity();
            else
                expired += c.getQuantity();
        }

        summary.put("totalCredits", totalCredits);
        summary.put("activeCredits", active);
        summary.put("expiredCredits", expired);

        return summary;
    }
}

package com.greenvest.service;

import com.greenvest.patterns.factory.*;
import com.greenvest.model.*;
import com.greenvest.repo.*;
import com.greenvest.rules.RuleEngineService;

import java.util.List;

public class SellerService {

    private ActionRepository actionRepo;
    private CreditRepository creditRepo;
    private RuleEngineService ruleEngine;

    public SellerService(ActionRepository aRepo,
                         CreditRepository cRepo,
                         RuleEngineService engine) {
        this.actionRepo = aRepo;
        this.creditRepo = cRepo;
        this.ruleEngine = engine;
    }

    public void submitAction(SustainabilityAction action) {
        actionRepo.save(action);
    }

    public Credit generateCredits(SustainabilityAction action) {
        if (action.isPending()){
            System.out.println("Action not approved yet.");
        return null;
    }
        Credit credit = CreditFactory.createCredit(
                action.getSellerEmail(),
                action.getType(),
                action.getMetricValue()
        );

        creditRepo.save(credit);
        return credit;
    }

    public boolean listCredits(User seller, Credit credit) {
        if (!ruleEngine.validateListing(seller, credit))
            return false;
        credit.list();
        System.out.println("DEBUG BEFORE UPDATE: listed = " + credit.isListed());
        creditRepo.updateCredit(credit);
        System.out.println("DEBUG AFTER UPDATE");
        // If valid  credit is considered listed
        return true;
    }



    public List<Credit> viewMyCredits(String sellerEmail) {
        return creditRepo.getBySeller(sellerEmail);
    }
}

package com.greenvest.util;
import java.util.regex.Pattern;

public class InputValidator {

    // Simple email pattern (OWASP-friendly for console apps)
    private static final Pattern EMAIL_PATTERN =
            Pattern.compile("^[A-Za-z0-9+_.-]+@[A-Za-z0-9.-]+$");

    // At least one letter, one number, min 6 chars
    private static final Pattern PASSWORD_PATTERN =
            Pattern.compile("^(?=.*[A-Za-z])(?=.*\\d).{6,}$");

    public static boolean isValidEmail(String email) {
        if (email == null) return false;
        email = email.trim();
        return !email.isEmpty() && EMAIL_PATTERN.matcher(email).matches();
    }

    public static boolean isValidPassword(String password) {
        if (password == null) return false;
        password = password.trim();
        return !password.isEmpty() && PASSWORD_PATTERN.matcher(password).matches();
    }
}

package com.greenvest.view;
import com.greenvest.config.SystemConfig;
import com.greenvest.controller.AdminController;
import com.greenvest.model.Activity;
import com.greenvest.model.SustainabilityAction;
import com.greenvest.model.User;
import com.greenvest.service.*;

import java.util.List;
import java.util.Scanner;
public class AdminView {

    private AdminController controller;
    private Scanner sc = new Scanner(System.in);

    public AdminView(AdminController controller) {
        this.controller = controller;
    }

    public void showDashboard(User admin) {

        while (true) {

            System.out.println("\n===== ADMIN DASHBOARD =====");
            System.out.println("1. View Pending Seller Actions");
            System.out.println("2. View Approved Actions");
            System.out.println("3. View Rejected Actions");
            System.out.println("4. Set Minimum Price");
            System.out.println("5. View System Activities");
            System.out.println("6. Logout");

            System.out.print("Select option: ");
            int choice = sc.nextInt();
            sc.nextLine();

            switch (choice) {
                case 1 -> viewPendingActions(admin);
                case 2 -> viewApprovedActions(admin);
                case 3 -> viewRejectedActions(admin);
                case 4 -> setMinimumPrice(admin);
                case 5 -> viewActivities();
                case 6 -> {
                    System.out.println("Logging out...");
                }
                default -> System.out.println("Invalid option.");
            }
        }
    }

    private void viewApprovedActions(User admin) {
        List<SustainabilityAction> list = controller.viewApproved(admin);

        if (list == null || list.isEmpty()) {
            System.out.println("No approved actions.");
        }

        System.out.println("===== APPROVED ACTIONS =====");
        list.forEach(a ->
                System.out.println(
                        "Seller: " + a.getSellerEmail() +
                                " | Type: " + a.getType() +
                                " | Metric: " + a.getMetricValue()
                )
        );
    }





    private void viewRejectedActions(User admin) {
        List<SustainabilityAction> list = controller.viewRejected(admin);

        if (list == null || list.isEmpty()) {
            System.out.println("No rejected actions.");

        }

        System.out.println("===== REJECTED ACTIONS =====");
        list.forEach(a ->
                System.out.println(
                        "Seller: " + a.getSellerEmail() +
                                " | Type: " + a.getType() +
                                " | Metric: " + a.getMetricValue()
                )
        );
    }

    private void viewActivities() {

        ActivityService service = new ActivityService();
        List<Activity> list = service.getAllActivities();

        if (list.isEmpty()) {
            System.out.println("No activities found.");
            return;
        }

        System.out.println("\n===== SYSTEM ACTIVITY LOG =====");
        for (Activity a : list) {
            System.out.println(
                    a.getTimestamp() + " | " +
                            a.getPerformedBy() + " | " +
                            a.getMessage()
            );
        }
    }

    private void setMinimumPrice(User admin) {

        System.out.print("Enter minimum credit price: ");
        double price = sc.nextDouble();
        sc.nextLine();

        if (price <= 0) {
            System.out.println("Invalid price.");
        }

        controller.setMinPrice(admin, price);

        System.out.println("Minimum credit price set to: " + price);
    }



    private void viewPendingActions(User admin) {

        List<SustainabilityAction> actions =
                controller.viewPending(admin);

        if (actions == null || actions.isEmpty()) {
            System.out.println("No pending seller actions.");
        }

        System.out.println("\n===== PENDING SELLER ACTIONS =====");

        for (int i = 0; i < actions.size(); i++) {
            SustainabilityAction a = actions.get(i);
            System.out.println(
                    i + ". Seller: " + a.getSellerEmail()
                            + " | Type: " + a.getType()
                            + " | Metric: " + a.getMetricValue()
            );
        }

        System.out.print("\nSelect action index (-1 to cancel): ");
        int index = sc.nextInt();
        sc.nextLine();

        if (index == -1) {
            System.out.println("Operation cancelled.");
            return;
        }

        SustainabilityAction selected = actions.get(index);

        System.out.println("\n1. Approve");
        System.out.println("2. Reject");
        System.out.println("0. Cancel");
        System.out.print("Enter choice: ");

        int choice = sc.nextInt();
        sc.nextLine();

        switch (choice) {
            case 1 -> {
                controller.approve(admin, selected);
                System.out.println(" Action approved and credits generated.");
            }
            case 2 -> {
                controller.reject(admin, selected);
                System.out.println("  Action rejected.");
            }
            case 0 -> System.out.println("Operation cancelled.");
            default -> System.out.println("Invalid option.");
        }
    }

}

package com.greenvest.view;
import com.greenvest.controller.BuyerController;
import com.greenvest.model.Credit;
import com.greenvest.model.Receipt;
import com.greenvest.model.User;
import com.greenvest.patterns.decorator.*;
import java.util.*;

import java.util.List;
import java.util.Scanner;

public class BuyerView {

    private BuyerController controller;
    private Scanner sc = new Scanner(System.in);

    public BuyerView(BuyerController controller) {
        this.controller = controller;
    }
    private void pause() {
        System.out.println("\nPress ENTER to continue...");
        sc.nextLine();
    }

    public void showDashboard(User buyer) {

        while (true) {

            System.out.println("\n===== BUYER DASHBOARD =====");
            System.out.println("Welcome, " + buyer.getName());
            System.out.println("1. View Available Credits");
            System.out.println("2. Purchase Credits");
            System.out.println("3. View Portfolio");
            System.out.println("4. View Receipts");
            System.out.println("5. View Account Summary");
            System.out.println("6. Logout");
            System.out.print("Enter choice: ");

            int choice = sc.nextInt();
            sc.nextLine();

            if (choice == 1) viewAvailableCredits(buyer);
            else if (choice == 2) handlePurchase(buyer);
            else if(choice == 3) showPortfolio(buyer);
            else if(choice == 4) showReceipt(buyer);
            else if(choice == 5 ) showAccountSummary(buyer);
            else if(choice <= 0 || choice > 6) System.out.println("Invalid choice, Try Again !");
            else return;
        }
    }

  private List<Credit> lastMarketplace = new ArrayList<>();

    private void viewAvailableCredits(User buyer) {
        lastMarketplace = controller.viewMarketplace(buyer);

        if (lastMarketplace == null || lastMarketplace.isEmpty()) {
            System.out.println("No credits available.");
            pause();

        }

        System.out.println("\n===== AVAILABLE CREDITS =====");
        for (int i = 0; i < lastMarketplace.size(); i++) {
            Credit c = lastMarketplace.get(i);
            System.out.println(
                    i + ". Seller: " + c.getSellerEmail() +
                            " | Qty: " + c.getQuantity() +
                            " | Price: " + c.getPrice()
            );
        }
        pause();
    }

    private void handlePurchase(User buyer) {

        if (lastMarketplace == null || lastMarketplace.isEmpty()) {
            System.out.println("No credits loaded. View marketplace first.");
            pause();
        }

        System.out.print("Select credit index: ");
        int index = sc.nextInt();
        sc.nextLine();

        if (index < 0 || index >= lastMarketplace.size()) {
            System.out.println("Invalid selection.");
            pause();
        }

        Credit selected = lastMarketplace.get(index);

        System.out.print("Quantity: ");
        int qty = sc.nextInt();
        sc.nextLine();

        Receipt receipt = controller.purchase(buyer, selected, qty);

        if (receipt != null) {
            System.out.println(" Purchase successful!");
            System.out.println("Receipt ID: " + receipt.getId());
            System.out.println("Total Amount: " + receipt.getTotalCost());
            pause();
        } else {
            System.out.println(" Purchase failed.");
            pause();
        }
    }

    private void showPortfolio(User buyer) {
        List<Credit> portfolio = controller.viewPortfolio(buyer);

        if (portfolio == null || portfolio.isEmpty()) {
            System.out.println("No credits in your portfolio.");
             pause();
        }


        for (Credit c : portfolio) {
            c.updateState();  // State Pattern
            System.out.println(
                    c.getId() + " | Qty: " + c.getQuantity() +
                            " | Price: " + c.getPrice() +
                            " | Status: " + c.getState()
            );
        }
        pause();
    }
    private void showReceipt(User buyer) {
        List<Receipt> receipts = controller.showReceipts(buyer);

        System.out.println("\n===== YOUR RECEIPTS =====");

    }
    private void showAccountSummary(User buyer) {

        Map<String, Object> summary = controller.getAccountSummary(buyer);

        if (summary == null) {
            System.out.println("Unable to load summary.");
            pause();
        }

        System.out.println("\n===== ACCOUNT SUMMARY =====");
        System.out.println("Buyer: " + buyer.getName());
        System.out.println("Available Balance: €" + summary.get("balance"));
        System.out.println("Total Credits Owned: " + summary.get("totalCredits"));
        System.out.println("Active Credits: " + summary.get("activeCredits"));
        System.out.println("Expired Credits: " + summary.get("expiredCredits"));
        pause();
    }



}

package com.greenvest.view;
import java.util.Scanner;
 import com.greenvest.util.InputValidator;
import com.greenvest.repo.*;
import com.greenvest.service.AdminService;
import com.greenvest.service.AuthService;
import com.greenvest.controller.*;
import com.greenvest.model.User;
import com.greenvest.service.BuyerService;
import com.greenvest.rules.*;
import com.greenvest.service.SellerService;
import com.greenvest.view.BuyerView;

public class MainMenu {

    private AuthService auth = new AuthService();

    public void start() {
        Scanner sc = new Scanner(System.in);
        while (true) {
            System.out.println("Select Role:");
            System.out.println("1. Admin");
            System.out.println("2. Seller");
            System.out.println("3. Buyer");
            int roleOption = sc.nextInt(); sc.nextLine();

            String role = switch (roleOption) {
                case 1 -> "ADMIN";
                case 2 -> "SELLER";
                case 3 -> "BUYER";
                default -> throw new IllegalArgumentException("Invalid role");
            };

            System.out.println("1. Register");
            System.out.println("2. Login");
            int option = sc.nextInt(); sc.nextLine();

            if (option == 1) {
                System.out.print("Enter SME Name: ");
                String name = sc.nextLine();
                System.out.print("Enter Company Email: ");
                String email = sc.nextLine();
                    if (!InputValidator.isValidEmail(email)) {
                        System.out.println("Invalid email format.");
                    }

                    System.out.print("Enter Password: ");
                    String pass = sc.nextLine();
                    if (!InputValidator.isValidPassword(pass)) {
                        System.out.println(
                                "Password must be at least 6 characters and contain letters and numbers."
                        );
                        continue;
                    }


                boolean success = auth.register(name, email, pass, role);
                System.out.println(success ? "Registration Successful" : "User already exists!");
            }

            if (option == 2) {
                System.out.print("Email: ");
                String email = sc.nextLine();
                if (!InputValidator.isValidEmail(email)) {
                    System.out.println("Invalid email format.");
                    continue;
                }

                System.out.print("Password: ");
                String pass = sc.nextLine();
                if (!InputValidator.isValidPassword(pass)) {
                    System.out.println(
                            "Password must be at least 6 characters and contain letters and numbers."
                    );
                    continue;
                }

                User user = auth.login(email, pass);


                if (user == null) {
                    System.out.println(" Invalid Login !!! ");
                    return;
                }

                switch (user.getRole()) {
                    case "ADMIN" -> {

                        ActionRepository actionRepo = ActionRepositoryJSON.getInstance();
                        CreditRepository creditRepo = CreditRepositoryJSON.getInstance();

                        AdminService adminService =
                                new AdminService(actionRepo, creditRepo);

                        AdminController adminController =
                                new AdminController(adminService);

                        AdminView adminView =
                                new AdminView(adminController);

                        adminView.showDashboard(user);
                    }
                    case "SELLER" -> {

                        ActionRepository actionRepo = ActionRepositoryJSON.getInstance();
                        CreditRepository creditRepo = CreditRepositoryJSON.getInstance();


                        RuleEngineService ruleEngine = new RuleEngineService();
                        ruleEngine.registerRule(new ListingRule());
                        ruleEngine.registerRule(new MinPriceRule());


                        // Service
                        SellerService sellerService =
                                new SellerService(actionRepo, creditRepo, ruleEngine);

                        //  Controller
                        SellerController sellerController =
                                new SellerController(sellerService);

                        //  View
                        SellerView sellerView =
                                new SellerView(sellerController);

                        sellerView.showDashboard(user);
                    }

                    case "BUYER" -> {

                        // Rule Engine
                        RuleEngineService engine = new RuleEngineService();
                        engine.registerRule(new ComplianceRule());


// Repositories (SINGLETONS)
                        CreditRepository creditRepo = CreditRepositoryJSON.getInstance();
                        ReceiptRepository receiptRepo = ReceiptRepositoryJSON.getInstance();
                        PortfolioRepository portfolioRepo = PortfolioRepositoryJSON.getInstance();

// Service
                        BuyerService service =
                                new BuyerService(creditRepo, receiptRepo, portfolioRepo, engine);

// Controller + View
                        BuyerController controller = new BuyerController(service);
                        BuyerView view = new BuyerView(controller);

                        view.showDashboard(user);
                    }
                }
            }
        }
    }

    public static void main(String[] args) {
        new MainMenu().start();
    }
}

package com.greenvest.view;

import com.greenvest.controller.SellerController;
import com.greenvest.model.Credit;
import com.greenvest.model.SustainabilityAction;
import com.greenvest.model.User;

import java.util.List;
import java.util.Scanner;
import java.util.UUID;

public class SellerView {

    private SellerController controller;
    private Scanner sc = new Scanner(System.in);

    public SellerView(SellerController controller) {
        this.controller = controller;
    }

    public void showDashboard(User seller) {

        while (true) {
            System.out.println("\n===== SELLER DASHBOARD =====");
            System.out.println("1. Submit Sustainability Action");
            System.out.println("2. View My Credits");
            System.out.println("3. List My Credits");
            System.out.println("4. Logout");

            int ch = sc.nextInt();
            sc.nextLine();

            if (ch == 1) submitAction(seller);
            else if (ch == 2) viewMyCredits(seller);
            else if (ch == 3)  listCredits(seller);
            else continue;
        }
    }

    private void submitAction(User seller) {
        System.out.print("Action Type (TREE/SOLAR): ");
        String type = sc.nextLine();

        System.out.print("Metric Value: ");
        double metric = sc.nextDouble();
        sc.nextLine();

        SustainabilityAction action =
                new SustainabilityAction(
                        UUID.randomUUID().toString(),
                        seller.getEmail(),
                        type,
                        metric
                );

        controller.submitAction(seller, action);
        System.out.println("Action submitted. Pending admin approval.");
    }

    private void viewMyCredits(User seller) {
        List<Credit> credits = controller.getMyCredits(seller);
        //  List<Credit> credits = controller.viewCredits(seller);

        if (credits == null || credits.isEmpty()) {
            System.out.println("No credits found.");
        }

        System.out.println("\n===== MY GENERATED CREDITS =====");

        for (int i = 0; i < credits.size(); i++) {
            Credit c = credits.get(i);

            System.out.println(
                    i + ". ID: " + c.getId() +
                            " | Qty: " + c.getQuantity() +
                            " | Price: " + c.getPrice() +
                            " | Expiry: " + c.getExpiry() +
                            " | State: " + c.getState()
            );
        }
    }
    private void generateCredits(User seller, SustainabilityAction action) {

        Credit credit = controller.generateCredits(seller, action);

        if (credit != null) {
            System.out.println("Credits generated successfully!");
            System.out.println("Quantity: " + credit.getQuantity());
            System.out.println("Status: " + credit.getState());
        }
    }
    private void listCredits(User seller) {

            //  Get seller’s generated credits
            List<Credit> credits = controller.getMyCredits(seller);

            if (credits == null || credits.isEmpty()) {
                System.out.println("No credits available to list.");

            }

            //  Show credits
            for (int i = 0; i < credits.size(); i++) {
                Credit c = credits.get(i);
                System.out.println(
                        i + ". ID: " + c.getId() +
                                " | Qty: " + c.getQuantity() +
                                " | Price: " + c.getPrice() +
                                " | State: " + c.getState()
                );
            }

            //  Select credit to list
            System.out.print("Select credit index to list: ");
            int index = sc.nextInt();
            sc.nextLine();

            Credit selectedCredit = credits.get(index);


            boolean listed = controller.listCredit(seller, selectedCredit);

            if (listed) {
                System.out.println(" Credit listed successfully");
            } else {
                System.out.println(" Credit listing failed");
            }
        }

    }
